import React, { useState } from "react";
import '../SelectDrop/select.css'


const Select = () => {

    const [isOpenSelect, setisOpenSelect] = useState(false);
    const openSelect=()=>{
        setisOpenSelect(!isOpenSelect);
    }
    const closeSelect=(index)=>{
        setisOpenSelect(!isOpenSelect);
    }

    return (
        <div className="selectDropWrapper cursor position-relative">
            <span className="openSelect" onClick={openSelect}>All Categories</span>
            {
                isOpenSelect === true &&
                <div className="selectedDrop">
            <div className="searchField">
                <input type="text"/>
            </div>
            <ul className="searchResults">
                    <li onClick={() => closeSelect} >Clothing & beauty</li>                   
                    <li onClick={() => closeSelect}>New & Featured</li>                   
                    <li onClick={() => closeSelect}>New & Featured</li>                   
                    <li onClick={() => closeSelect}>New & Featured</li>                   
                    <li onClick={() => closeSelect}>New & Featured</li>                   
                    <li onClick={() => closeSelect}>New & Featured</li>                   
                    <li onClick={() => closeSelect}>New & Featured</li>                   
                    <li onClick={() => closeSelect}>Tennis</li>
                    <li onClick={() => closeSelect}>Football</li>
                    <li onClick={() => closeSelect}>Basketball</li>
                    <li onClick={() => closeSelect}>Men</li>
                    <li onClick={() => closeSelect}>Women</li>
                    <li onClick={() => closeSelect}>Kids</li>
                    <li onClick={() => closeSelect}>Sale</li>
            </ul>
        </div>
            }
</div>
    )
}
export default Select;
