import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import './style.css'; 
import Catslider from '../../Components/catslider';

const DetailsPage = () => {
    const { id } = useParams(); // Lấy ID sản phẩm từ URL
    const [product, setProduct] = useState(null);
    const [mainImage, setMainImage] = useState("");

    useEffect(() => {
        const fetchProduct = async () => {
          try {
            const response = await axios.get(`http://localhost:5001/api/products/${id}`);
            console.log('Product data:', response.data);
            setProduct(response.data);
          } catch (error) {
            console.error("Error fetching product:", error.response || error);
          }
        };
      
        fetchProduct();
      }, [id]);
    if (!product) {
        return <div>Loading...</div>;
    }

    return (
        <>
            <section id="prodetails" className="section-1">
                <div className="single-pro-image">
                    <img src={mainImage} width="100%" id="MainImg" alt={product.name} />
                    <div className="small-img-group">
                        {product.images && product.images.map((image, index) => (
                            <div key={index} className="small-img-col">
                                <img 
                                    src={image} 
                                    width="100%" 
                                    className="small-img" 
                                    onClick={() => setMainImage(image)} 
                                    alt={`Product thumbnail ${index + 1}`} 
                                />
                            </div>
                        ))}
                    </div>
                </div>
                <div className="single-pro-details">
                    <h6>Home / {product.category}</h6>
                    <h4>{product.name}</h4>
                    <h2>{product.price} đ</h2>
                    <select>
                        <option>Select Size</option>
                        {product.sizes && product.sizes.map((size, index) => (
                            <option key={index}>{size}</option>
                        ))}
                    </select>
                    <input type="number" />
                    <button className="btn">Add To Cart</button>
                    <h4>Product Details</h4>
                    <span>{product.description}</span>
                </div>
            </section>
            <Catslider />
        </>
    );
};

export default DetailsPage;
