import React, { useState, useEffect } from "react";
import './style.css';
import { useParams } from "react-router-dom";
import axios from 'axios';
import CatSlider from "../../Components/catslider/index";

const DetailsPage = () => {
    const { productId } = useParams();  
    const [product, setProduct] = useState(null);  
    const [loading, setLoading] = useState(true);  
    const [mainImage, setMainImage] = useState("");  // Định nghĩa state cho ảnh chính

    useEffect(() => {
        axios.get(`http://localhost:5001/api/products/${productId}`)
            .then(response => {
                setProduct(response.data);  
                setMainImage(response.data.image);  
                setLoading(false);  
            })
            .catch(error => {
                console.error("Lỗi khi lấy chi tiết sản phẩm:", error);
                setLoading(false);  
            });
    }, [productId]);  

    if (loading) {
        return <div>Đang tải thông tin sản phẩm...</div>;
    }

    if (!product) {
        return <div>Sản phẩm không tồn tại</div>;
    }

    return (
        <>
        <section id="prodetails" className="section-1">
            <div className="single-pro-image">
                <img src={`/Images/${mainImage}`} width="100%" id="MainImg" alt={product.name} />
                <div className="small-img-group">
                    {product.images && product.images.map((image, index) => (
                        <div key={index} className="small-img-col">
                            <img 
                                src={`/Images/${image}`} 
                                width="100%" 
                                className="small-img" 
                                alt={`small-image-${index}`}
                                onClick={() => setMainImage(image)}  
                            />
                        </div>
                    ))}
                </div>
            </div>
            <div className="single-pro-details">
                <h6>Home / Sneaker</h6>
                <h4>{product.name}</h4>
                <h2>{product.price}</h2>
                <select>
                    <option>Select Size</option>
                    {product.sizes && product.sizes.map((size, index) => (
                        <option key={index}>{size}</option>
                    ))}
                </select>
                <input type="number" />
                <button className="btn">Add To Cart</button>
                <h4>Product Details</h4>
                <span>{product.description}</span>
            </div>
        </section>
        <CatSlider/>
        </>
    );
};

export default DetailsPage;
