import React, { useState } from "react";
import '../SelectDrop/select.css'
import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown';
import { ClickAwayListener } from '@mui/base';
import LocationOnOutlinedIcon from '@mui/icons-material/LocationOn';


const Select = ({data,placeholder}) => {
    const [isOpenSelect, setisOpenSelect] = useState(false);
    const [selectedIndex, setselectedIndex] = useState(0);
    const [selectedItem, setselectedItem] = useState(placeholder);

    const openSelect=()=>{
        setisOpenSelect(!isOpenSelect);
    }
    const closeSelect=(index, name)=>{
        setselectedIndex(index);
        setisOpenSelect(false);
        setselectedItem(name)
    }

    return (
        <ClickAwayListener onClickAway={()=> setisOpenSelect(false)}>
        <div className="selectDropWrapper cursor position-relative">
            <LocationOnOutlinedIcon style={{opacity:'0.7'}}/>
            <span className="openSelect" onClick={openSelect}>{selectedItem}
                <KeyboardArrowDownIcon className="arrow"/></span>
            {
                isOpenSelect === true &&
                <div className="selectedDrop">
            <div className="searchField">
                <input type="text" placeholder="Search here...."/>
            </div>
            <ul className="searchResults">
            <li key={0} onClick={() => closeSelect(0, placeholder)} className={`${selectedIndex=== 0 ? 'active' :''}`} >{placeholder}</li>
                    {
                        data.map((item,index)=>{
                            return(
                                <li key={index+1} onClick={() => closeSelect(index+1, item)} className={`${selectedIndex=== index+1 ? 'active' :''}`} >{item}</li>
                            )
                        })
                    }
            </ul>
        </div>
            }
        </div>
        </ClickAwayListener>
    )
}
export default Select;
