import React, { createContext, useState, useContext, useEffect } from 'react';

const CartContext = createContext();

export const useCart = () => useContext(CartContext);

export const CartProvider = ({ children }) => {
  // Lấy giỏ hàng từ localStorage khi component được mount
  const [cart, setCart] = useState(() => {
    const savedCart = localStorage.getItem('cart');
    return savedCart ? JSON.parse(savedCart) : [];
  });

  // Thêm sản phẩm vào giỏ hàng
  const addToCart = (product, quantity, size) => {
    const updatedCart = [...cart];
    const productIndex = updatedCart.findIndex(item => item.product._id === product._id && item.size === size);
    if (productIndex === -1) {
      updatedCart.push({ product, quantity, size });
    } else {
      updatedCart[productIndex].quantity += quantity;
    }
    setCart(updatedCart);
    localStorage.setItem('cart', JSON.stringify(updatedCart));  // Lưu giỏ hàng vào localStorage
  };

  // Xoá sản phẩm khỏi giỏ hàng
  const removeFromCart = (productId, size) => {
    const updatedCart = cart.filter(item => item.product._id !== productId || item.size !== size);
    setCart(updatedCart);
    localStorage.setItem('cart', JSON.stringify(updatedCart));  // Cập nhật giỏ hàng trong localStorage
  };

  // Cập nhật số lượng sản phẩm
  const updateQuantity = (productId, quantity, size) => {
    const updatedCart = cart.map(item => 
        item.product._id === productId && item.size === size ? { ...item, quantity } : item
    );
    setCart(updatedCart);
    localStorage.setItem('cart', JSON.stringify(updatedCart));  // Lưu giỏ hàng sau khi cập nhật
  };

  // Lấy tổng số sản phẩm trong giỏ
  const getCartItemCount = () => {
    return cart.reduce((acc, item) => acc + item.quantity, 0);
  };

  // Cập nhật giỏ hàng khi component mount
  useEffect(() => {
    const savedCart = localStorage.getItem('cart');
    if (savedCart) {
      setCart(JSON.parse(savedCart));
    }
  }, []);  // Dùng useEffect để đảm bảo lấy giỏ hàng khi component mount

  // Xóa toàn bộ giỏ hàng
  const clearCart = () => {
    setCart([]);
    localStorage.removeItem('cart');  // Xóa giỏ hàng khỏi localStorage
  };

  return (
    <CartContext.Provider value={{ cart, addToCart, removeFromCart, updateQuantity, getCartItemCount, clearCart }}>
      {children}
    </CartContext.Provider>
  );
};
