import React from "react";
import Slider from "react-slick"
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";
import banner1 from '../../../src/Assets/Images/blog 4.jpg'
import banner2 from '../../../src/Assets/Images/blog 5.jpg'
import banner3 from '../../../src/Assets/Images/blog 6.jpg'
import banner4 from '../../../src/Assets/Images/blog 7.jpg'
import banner5 from '../../../src/Assets/Images/blog 3.jpg'



const CatSlider =()=>{
      const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 3,
        slidesToScroll: 1,
        responsive: [
          {
            breakpoint: 768, // Adjust for smaller devices
            settings: {
              slidesToShow: 1,
              slidesToScroll: 1,
            },
          },
        ],
      };
    
    return(
        <div className="catSliderSection">
            <div className="container-fluid">
                <h2 className="hd">featured</h2>
                <Slider {...settings} className="cat_slider_Main">
                {/* Slide 1 */}
        <div style={{ padding: "10px" }}>
          <img
            src="banner1" // Đường dẫn đến ảnh chú mèo
            alt="Nothing Beats The CITY"
            style={{
              width: "100%",
              borderRadius: "10px",
            }}
          />
          <h3>Nothing Beats The CITY</h3>
          <button
            style={{
              background: "#000",
              color: "#fff",
              padding: "10px",
              border: "none",
              cursor: "pointer",
              borderRadius: "5px",
            }}
          >
            Shop Now
          </button>
        </div>

        {/* Slide 2 */}
        <div style={{ padding: "10px" }}>
          <img
            src="path-to-runner-image.jpg" // Đường dẫn đến ảnh người chạy bộ
            alt="Nike Pegasus 41 PRM"
            style={{
              width: "100%",
              borderRadius: "10px",
            }}
          />
          <h3>Nike Pegasus 41 PRM</h3>
          <button
            style={{
              background: "#000",
              color: "#fff",
              padding: "10px",
              border: "none",
              cursor: "pointer",
              borderRadius: "5px",
            }}
          >
            Shop Now
          </button>
        </div>

        {/* Slide 3 */}
        <div style={{ padding: "10px" }}>
          <img
            src="path-to-shoes-image.jpg" // Đường dẫn đến ảnh giày
            alt="Mercurial Dream Speed"
            style={{
              width: "100%",
              borderRadius: "10px",
            }}
          />
          <h3>Mercurial Dream Speed</h3>
          <button
            style={{
              background: "#000",
              color: "#fff",
              padding: "10px",
              border: "none",
              cursor: "pointer",
              borderRadius: "5px",
            }}
          >
            Shop Now
          </button>
        </div>
                </Slider>
            </div>
        </div>)
}
export default CatSlider;
