import React, { useState } from "react";
import "./Header.css";
import { Link } from "react-router-dom";
import Button from "@mui/material/Button";

const Header = () => {
    const [showDropdown, setShowDropdown] = useState(false);

    const toggleDropdown = () => setShowDropdown(!showDropdown);

    return (
        <section className="header">
            <a href="#" className="logo">
                2T Store<span>.</span>
            </a>
            <nav className="navbar">
                <Button><Link to="/">Home</Link></Button>
                <div>
                <Button><Link to="/Listing">Shop</Link></Button>
                
                </div>
                {/* Dropdown */}
                <div
                    className="dropdown"
                    onMouseEnter={toggleDropdown}
                    onMouseLeave={toggleDropdown}
                >
                    <Button><Link>Giới Thiệu</Link></Button>
                    {showDropdown && (
                        <div className="dropdown-content">
                            <Button><Link to="/about">About Us</Link></Button>
                            <Button><Link to="/team">Our Team</Link></Button>
                            <Button><Link to="/services">Services</Link></Button>
                        </div>
                    )}
                </div>

                <Button><Link>Liên Hệ</Link></Button>
                <Button><Link to="/Blog">Blog</Link></Button>
                <Button><Link>Đăng Ký/Đăng Nhập</Link></Button>
            </nav>
            <div className="icons">
                <a href="#" className="fas fa-heart"></a>
                <a href="#" className="fas fa-shopping-cart"></a>
                <a href="#" className="fas fa-solid fa-user"></a>
            </div>
        </section>
    );
};

export default Header;
