import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const DetailsPage = () => {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [mainImage, setMainImage] = useState("");

  useEffect(() => {
    axios.get(`http://localhost:5001/api/products/${id}`)
      .then((response) => {
        const productData = response.data;
        setProduct(productData);
        setMainImage(productData.image); // Ảnh chính
      })
      .catch((error) => console.error("Error fetching product:", error));
  }, [id]);

  if (!product) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <section id="prodetails" className="section-1">
        <div className="single-pro-image">
          <img src={`/Images/${mainImage}`} width="100%" id="MainImg" alt={product.name} />
          <div className="small-img-group">
            {product.images.map((image, index) => (
              <div key={index} className="small-img-col">
                <img
                  src={`/Images/${image}`}
                  width="100%"
                  className="small-img"
                  onClick={() => setMainImage(image)}
                  alt={`Product thumbnail ${index + 1}`}
                />
              </div>
            ))}
          </div>
        </div>
        <div className="single-pro-details">
          <h6>Home / {product.category}</h6>
          <h4>{product.name}</h4>
          <h2>{product.price} đ</h2>
          <select>
            <option>Select Size</option>
            {product.sizes.map((size, index) => (
              <option key={index}>{size}</option>
            ))}
          </select>
          <input type="number" min="1" />
          <button className="btn">Add To Cart</button>
          <h4>Product Details</h4>
          <span>{product.description}</span>
        </div>
      </section>
    </div>
  );
};

export default DetailsPage;
