import React, { useState } from "react";
import "./Header.css";
import { Link } from "react-router-dom";
import Button from "@mui/material/Button";
import ArrowDropDownOutlinedIcon from '@mui/icons-material/ArrowDropDownOutlined';

const Header = () => {
    const [showDropdown, setShowDropdown] = useState(false);

    const toggleDropdown = () => setShowDropdown(!showDropdown);

    return (
        <section className="header">
            <a href="#" className="logo">
                2T Store<span>.</span>
            </a>
            <nav className="navbar">
                <Button><Link to="/">Home</Link></Button>
                <div 
                className="dropdown"
                onMouseEnter={toggleDropdown}
                onMouseLeave={toggleDropdown}
                >
                <Button><Link to="/listing">Shop</Link><ArrowDropDownOutlinedIcon/></Button>
                {showDropdown && (
                        <div className="dropdown-content">
                            <Button><Link to="/about">New Products</Link></Button>
                            <Button><Link to="/team">Feature Products</Link></Button>
                            <Button><Link to="/services">Best Sellers</Link></Button>
                        </div>
                    )}
                </div>
                {/* Dropdown */}
                <Button><Link>Giới Thiệu</Link></Button>
                <Button><Link>Liên Hệ</Link></Button>
                <Button><Link to="/Blog">Blog</Link></Button>
                <Button><Link>Đăng Ký/Đăng Nhập</Link></Button>
            </nav>
            <div className="icons">
                <a href="#" className="fas fa-heart"></a>
                <a href="#" className="fas fa-shopping-cart"></a>
                <a href="#" className="fas fa-solid fa-user"></a>
            </div>
        </section>
    );
};

export default Header;
