import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Profile.css'

function Profile() {
    const [userData, setUserData] = useState({
        name: '',
        email: '',
        password: ''
    });

    useEffect(() => {
        // Kiểm tra xem người dùng có đăng nhập hay chưa (có token trong localStorage không)
        const token = localStorage.getItem('token');
        if (!token) {
            // Nếu không có token, chuyển hướng người dùng đến trang đăng nhập
            window.location.href = '/login';
        } else {
            // Nếu có token, tiếp tục gọi API để lấy dữ liệu người dùng
            axios.get('/api/users/profile', {
                headers: { Authorization: `Bearer ${token}` }
            }).then(response => {
                setUserData(response.data);
            }).catch(error => {
                console.error("Error fetching user data:", error);
            });
        }
    }, []);

    if (!userData) return <div>Loading...</div>;

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setUserData(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.put('/api/users/profile', userData, {
                headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
            });
            alert("Profile updated successfully");
        } catch (error) {
            console.error("Error updating profile:", error);
        }
    };

    return (
        <div className="profile-container">
            <div className="profile-card">
                <h1 className="profile-header">Profile</h1>
                <form onSubmit={handleSubmit}>
                    <div className="profile-info">
                        <label>Name</label>
                        <input
                            type="text"
                            name="name"
                            value={userData.name}
                            onChange={handleInputChange}
                        />
                    </div>
                    <div className="profile-info">
                        <label>Email</label>
                        <input
                            type="email"
                            name="email"
                            value={userData.email}
                            onChange={handleInputChange}
                        />
                    </div>
                    <div className="profile-info">
                        <label>Password</label>
                        <input
                            type="password"
                            name="password"
                            value={userData.password}
                            onChange={handleInputChange}
                        />
                    </div>
                    <button type="submit" className="profile-edit-button">Update Profile</button>
                </form>
            </div>
        </div>
    );
}

export default Profile;
