import React, { useState,useEffect } from "react";
import './Header.css'
import SearchIcon from '@mui/icons-material/Search';
import Select from '../SelectDrop/select';
import axios from 'axios';
import LocationOnOutlinedIcon from '@mui/icons-material/LocationOnOutlined';
import iconHeart from '../../Assets/Images/favorite.svg'
import ShoppingCart from '../../Assets/Images/shopping-cart.svg'
import User from '../../Assets/Images/user.svg'
import { Favorite } from "@mui/icons-material";





const Header = () => {
    const [categories, setcategories] = useState([
        'New & Featured',
        'New & Featured',
        'New & Featured',
        'New & Featured',
        'New & Featured',
        'New & Featured',
        'New & Featured',
        'Tennis',
        'Football',
        'Basketball',
        'Men',
        'Women',
        'Kids',
        'Sale'
    ])

    const countryList=[];
    useEffect (() =>{
        getCountry('https://countriesnow.space/api/v0.1/countries/');
    },[]);
    const getCountry=async(url) => {
        try{
            await axios.get(url).then((res) => {
                if(res!==null){
                    //console.log(res.data.data);
                    res.data.data.map((item,index)=>{
                        countryList.push(item.country);
                        //console.log(item.country)
                    })
                    //console.log(res.data.data)
                }
            })
        }catch(error){
            console.log(error.message);
        }
    } 




    return (
        <header>
            <div className="container-fluid">
                <div className="row">
                    <div className="col-sm-5">
                        <a href="#" class="logo">2T Store<span>.</span></a>
                    </div>
                    {/*header search */}
                    <div className="col-sm-5">
                        <div className="headerSearch d-flex align-items-center">
                        <Select data={categories} placeholder={'All Categories'} icon={false}/>

                            <div className="search">
                                <input type="text" placeholder="Search for item..."></input>
                                <SearchIcon className="searchIcon"/>
                            </div>
                        </div>
                    </div>
                    {/*header search */}


        <div className='col-sm-5 d-flex align-items-center'>
            <div className="ml-auto d-flex align-items-center">
            <div className="countryWrapper">
            <Select data={countryList} placeholder={'Your Location'} icon={ <LocationOnOutlinedIcon style={{opacity:'0.7'}}/>}/>
            </div>
            <ul className="list list-inline-mb-0">
                <li className="list-inline-item mb-0 headerTabs">
                    <span className="favorite">
                        <img src={iconHeart}/>
                        <span className="badge bg-success rounded-circle">3</span>
                        Favorite
                    </span>
                </li>
            </ul>
            </div>
        </div>

                </div>
            </div>
        </header>
    )
}
export default Header;
