import React, { useState, useEffect } from "react";
import "./Header.css";
import { Link } from "react-router-dom";
import Button from "@mui/material/Button";
import ArrowDropDownOutlinedIcon from '@mui/icons-material/ArrowDropDownOutlined';
import SearchOutlinedIcon from '@mui/icons-material/SearchOutlined';
import FavoriteBorderOutlinedIcon from '@mui/icons-material/FavoriteBorderOutlined';
import ShoppingBagOutlinedIcon from '@mui/icons-material/ShoppingBagOutlined';
import { useCart } from '../../Pages/Cart/CartContext';

const Header = () => {
    const { getCartItemCount, clearCart } = useCart();  // Lấy số lượng sản phẩm trong giỏ hàng và hàm xóa giỏ hàng
    const [cartItemCount, setCartItemCount] = useState(getCartItemCount());  // Lưu số lượng sản phẩm trong giỏ

    // Cập nhật lại số lượng giỏ hàng khi cart thay đổi
    useEffect(() => {
        setCartItemCount(getCartItemCount());
    }, [getCartItemCount()]);

    const handleClearCart = () => {
        clearCart();  // Xóa giỏ hàng
        setCartItemCount(0);  // Cập nhật số lượng giỏ hàng thành 0
    };

    return (
        <section className="header">
            <a href="#" className="logo">
                2T Store<span>.</span>
            </a>
            <nav className="navbar">
                <Button><Link to="/">Home</Link></Button>
                <div 
                className="dropdown"
                onMouseEnter={() => setShowDropdown(true)}
                onMouseLeave={() => setShowDropdown(false)}
                >
                <Button><Link to="/listing">Shop</Link><ArrowDropDownOutlinedIcon/></Button>
                </div>
                <Button><Link>Giới Thiệu</Link></Button>
                <Button><Link>Liên Hệ</Link></Button>
                <Button><Link to="/Blog">Blog</Link></Button>
                <div className="dropdown"
                onMouseEnter={() => setShowDropdown(true)}
                onMouseLeave={() => setShowDropdown(false)}>
                <Button><Link>Sports</Link><ArrowDropDownOutlinedIcon/></Button>
                </div>
            </nav>
            <div className="icons">
                <div className="search-box">
                    <SearchOutlinedIcon />
                    <input type="text" placeholder="Search" />
                </div>
                <FavoriteBorderOutlinedIcon className="icon" />
                <div className="cart">
                    <Link to="/Cart">
                        <ShoppingBagOutlinedIcon className="icon"/>
                        {cartItemCount > 0 && (
                            <span className="cart-count">{cartItemCount}</span>
                        )}
                    </Link>
                    {/* Nút để xóa giỏ hàng */}
                    <button onClick={handleClearCart}>Clear Cart</button>
                </div>
            </div>
        </section>
    );
};

export default Header;
