import React, { useState } from "react";
import '../SelectDrop/select.css'
import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown';

const Select = () => {

    const [isOpenSelect, setisOpenSelect] = useState(false);
    const [selectedIndex, setselectedIndex] = useState(0);
    const openSelect=()=>{
        setisOpenSelect(!isOpenSelect);
    }
    const closeSelect=(index)=>{
        setselectedIndex(index);
        setisOpenSelect(false);
    }

    return (
        <div className="selectDropWrapper cursor position-relative">
            <span className="openSelect" onClick={openSelect}>All Categories <KeyboardArrowDownIcon className="arrow"/></span>
            {
                isOpenSelect === true &&
                <div className="selectedDrop">
            <div className="searchField">
                <input type="text"/>
            </div>
            <ul className="searchResults">
                    <li onClick={() => closeSelect(0)} className={`${selectedIndex===0 ? 'active' :''}`} >All Categories</li>
                    <li onClick={() => closeSelect(1)} className={`${selectedIndex===1 ? 'active' :''}`}>Clothing & beauty</li>                   
                    <li onClick={() => closeSelect(2)} className={`${selectedIndex===2 ? 'active' :''}`}>New & Featured</li>                   
                    <li onClick={() => closeSelect(3)} className={`${selectedIndex===3 ? 'active' :''}`}>New & Featured</li>                   
                    <li onClick={() => closeSelect(4)} className={`${selectedIndex===4 ? 'active' :''}`}>New & Featured</li>                   
                    <li onClick={() => closeSelect(5)} className={`${selectedIndex===5 ? 'active' :''}`}>New & Featured</li>                   
                    <li onClick={() => closeSelect(6)} className={`${selectedIndex===6 ? 'active' :''}`}>New & Featured</li>                   
                    <li onClick={() => closeSelect(7)} className={`${selectedIndex===7 ? 'active' :''}`}>New & Featured</li>                   
                    <li onClick={() => closeSelect(8)} className={`${selectedIndex===8 ? 'active' :''}`}>Tennis</li>
                    <li onClick={() => closeSelect(9)} className={`${selectedIndex===9 ? 'active' :''}`}>Football</li>
                    <li onClick={() => closeSelect(10)} className={`${selectedIndex===10 ? 'active' :''}`}>Basketball</li>
                    <li onClick={() => closeSelect(11)} className={`${selectedIndex===11 ? 'active' :''}`}>Men</li>
                    <li onClick={() => closeSelect(12)} className={`${selectedIndex===12 ? 'active' :''}`}>Women</li>
                    <li onClick={() => closeSelect(13)} className={`${selectedIndex===13 ? 'active' :''}`}>Kids</li>
                    <li onClick={() => closeSelect(14)} className={`${selectedIndex===14 ? 'active' :''}`}>Sale</li>
            </ul>
        </div>
            }
</div>
    )
}
export default Select;
