import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Checkout.css';

const CheckoutPage = () => {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    name: '',
    address: '',
    phone: '',
    email: '',
  });
  const [paymentMethod, setPaymentMethod] = useState('Cash');
  const cart = JSON.parse(localStorage.getItem('cart')) || [];

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const totalAmount = cart.reduce((acc, item) => acc + item.quantity * item.price, 0);
      const orderItems = cart.map(item => ({
        product: item.product._id,  // Lấy ObjectId từ sản phẩm
        size: item.size,
        quantity: item.quantity,
        price: item.product.price,
        subtotal: item.product.price * item.quantity,  // Tính subtotal cho mỗi item
      }));
      const response = await axios.post('http://localhost:5001/api/orders', {
        customer: form,
        items: orderItems,
        totalAmount,
        paymentMethod,
      });
      if (response.status === 201) {
        localStorage.removeItem('cart');
        navigate('/order-success');
      }
    } catch (error) {
      console.error('Failed to create order:', error);
    }
  };

  return (
    <div className="checkout-container">
      <form className="checkout-form" onSubmit={handleSubmit}>
        <h1 className="checkout-title">Checkout</h1>
        <div className="form-group">
          <label htmlFor="name">Name</label>
          <input
            id="name"
            type="text"
            placeholder="Name"
            value={form.name}
            onChange={(e) => setForm({ ...form, name: e.target.value })}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="address">Address</label>
          <input
            id="address"
            type="text"
            placeholder="Address"
            value={form.address}
            onChange={(e) => setForm({ ...form, address: e.target.value })}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="phone">Phone</label>
          <input
            id="phone"
            type="text"
            placeholder="Phone"
            value={form.phone}
            onChange={(e) => setForm({ ...form, phone: e.target.value })}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="email">Email</label>
          <input
            id="email"
            type="email"
            placeholder="Email"
            value={form.email}
            onChange={(e) => setForm({ ...form, email: e.target.value })}
          />
        </div>
        <div className="form-group">
          <label htmlFor="payment-method">Payment Method</label>
          <select
            id="payment-method"
            value={paymentMethod}
            onChange={(e) => setPaymentMethod(e.target.value)}
          >
            <option value="Cash">Cash on Delivery</option>
            <option value="Credit Card">Credit Card</option>
          </select>
        </div>
        <button className="submit-btn" type="submit">
          Place Order
        </button>
      </form>
    </div>
  );
};

export default CheckoutPage;
