import React, { useState, useEffect } from "react";
import './Cart.css'
import { useCart } from '../Cart/CartContext';  // Import useCart từ CartContext

const Cart = () => {
  const { cart, removeFromCart, updateQuantity } = useCart();  // Lấy cart và các hàm từ CartContext
  const [cartTotal, setCartTotal] = useState(0);

  // Tính toán tổng giá trị giỏ hàng
  useEffect(() => {
    const total = cart.reduce((acc, item) => {
      const price = parseFloat(item.product.price.toString().replace(/\./g, '').replace('đ', '')); // Chuyển đổi giá
      return acc + price * item.quantity;
    }, 0);
    setCartTotal(total); // Cập nhật tổng giá trị
  }, [cart]);

  // Xử lý thay đổi số lượng sản phẩm
  const handleQuantityChange = (productId, quantity) => {
    if (quantity > 0) {
      updateQuantity(productId, quantity);  // Gọi hàm updateQuantity để cập nhật số lượng
    }
  };

  return (
    <>
      <h1 className="heading">Shopping <span>Cart</span></h1>
      <section id="cart" className="section-p1">
        <table width="100%">
          <thead>
            <tr>
              <td>Remove</td>
              <td>Image</td>
              <td>Product</td>
              <td>Price</td>
              <td>Quantity</td>
              <td>Subtotal</td>
            </tr>
          </thead>
          <tbody>
            {cart.length > 0 ? (
              cart.map((item, index) => (
                <tr key={index}>
                  <td><a href="#" onClick={() => removeFromCart(item.product._id)}><i className="far fa-times-circle"></i></a></td>
                  <td><img src={`/Images/${item.product.image}`} alt={item.product.name} /></td>
                  <td>{item.product.name}</td>
                  <td>{item.product.price}</td>
                  <td>
                    <input
                      type="number"
                      value={item.quantity}
                      min="1"
                      onChange={(e) => handleQuantityChange(item.product._id, Number(e.target.value))}
                    />
                  </td>
                  <td>{parseFloat(item.product.price.toString().replace('đ', '')) * item.quantity}đ</td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="6">Giỏ hàng trống</td>
              </tr>
            )}
          </tbody>
        </table>
      </section>

      <section id="cart-add" className="section-p1">
        <div id="coupon">
          <h3>Apply Coupon</h3>
          <div>
            <input type="text" placeholder="Enter Your Coupon" />
            <button className="normal">Apply</button>
          </div>
        </div>

        <div id="subtotal">
          <h3>Cart Total</h3>
          <table>
            <tr>
              <td>Cart Subtotal</td>
              <td>{cartTotal.toLocaleString()}đ</td>
            </tr>
            <tr>
              <td>Shipping</td>
              <td>Free</td>
            </tr>
            <tr>
              <td><strong>Total</strong></td>
              <td><strong>{cartTotal.toLocaleString()}đ</strong></td>
            </tr>
          </table>
          <button className="normal">Proceed to checkout</button>
        </div>
      </section>
    </>
  );
};

export default Cart;
